mapper.select:
select

from rlab_common_info.t_common_banner

  TRUE

    and id = #{id,jdbcType=BIGINT}


    and b_id = #{bId,jdbcType=BIGINT}


    and b_seq = #{bSeq,jdbcType=INTEGER}


    and b_title = #{bTitle,jdbcType=VARCHAR}


    and b_pic = #{bPic,jdbcType=VARCHAR}


    and b_link = #{bLink,jdbcType=VARCHAR}


    and b_link_type = #{bLinkType,jdbcType=INTEGER}


    and  b_position = #{bPosition,jdbcType=INTEGER}


    and b_state != #{bState,jdbcType=SMALLINT}


    and creat_time >= #{creatTime,jdbcType=TIMESTAMP}


    and modify_time >= #{modifyTime,jdbcType=TIMESTAMP}


ORDER BY b_state DESC ,b_seq ASC ,modify_time ASC

select

from rlab_common_info.t_common_banner

  TRUE

    and  b_position = #{position,jdbcType=INTEGER}


    and b_state = #{state,jdbcType=SMALLINT}


ORDER BY b_state DESC ,b_seq ASC ,modify_time ASC
